Windows Terminal#win+r 输入wt开启#ctrl+shift+t 打开新标签 ctrl+alt+1 打开第一个标签(配置json后改为ctrl+n) shift+alt+ +/- 水平垂直拆分 shift+alt+ ↑↓←→ 水平垂直调整大小(需先拆分)
win+1234 调出状态栏的第几个应用,无论时候打开
json#配置 https://learn.microsoft.com/zh-cn/windows/terminal/customize-settings/actions 快捷键尽量跟浏览器一致 { &#34;keys&#34;: &#34;ctrl+`&#34;, &#34;id&#34;: &#34;Terminal.OpenNewTab&#34; }, { &#34;keys&#34;: &#34;ctrl+1&#34;, &#34;id&#34;: &#34;Terminal.SwitchToTab0&#34; }, { &#34;keys&#34;: &#34;ctrl+2&#34;, &#34;id&#34;: &#34;Terminal.SwitchToTab1&#34; }, { &#34;keys&#34;: &#34;ctrl+3&#34;, &#34;id&#34;: &#34;Terminal.SwitchToTab2&#34; }, { &#34;keys&#34;: &#34;ctrl+4&#34;, &#34;id&#34;: &#34;Terminal.SwitchToTab3&#34; }, { &#34;keys&#34;: &#34;ctrl+5&#34;, &#34;id&#34;: &#34;Terminal.SwitchToTab4&#34; }, { &#34;keys&#34;: &#34;ctrl+6&#34;, &#34;id&#34;: &#34;Terminal.SwitchToTab5&#34; }, { &#34;keys&#34;: &#34;ctrl+7&#34;, &#34;id&#34;: &#34;Terminal.SwitchToTab6&#34; }, { &#34;keys&#34;: &#34;ctrl+8&#34;, &#34;id&#34;: &#34;Terminal.SwitchToTab7&#34; }

代码编译#main.go#go build main.go package mainimport (&#34;fmt&#34;&#34;io&#34;&#34;net/http&#34;&#34;net/url&#34;&#34;os&#34;&#34;os/exec&#34;&#34;strings&#34;&#34;time&#34;)func main() {Init()server := &amp;http.Server{Addr: &#34;0.0.0.0:8888&#34;,}mux := http.NewServeMux()mux.HandleFunc(&#34;/get&#34;, get)mux.HandleFunc(&#34;/post&#34;, post)mux.HandleFunc(&#34;/m3u8er&#34;, m3u8er)mux.HandleFunc(&#34;/noter&#34;, noter)server.Handler = mux_ = server.ListenAndServe()}func Init() {dirs := []string{&#34;/home/nginx/m3u8/&#34;, &#34;/home/nginx/note/&#34;}for _, dir := range dirs {_, err := os.Stat(dir)if err == nil {_ = os.MkdirAll(dir, 0777)}}}// 获取客户端信息func get(w http.ResponseWriter, r *http.Request) {write := &#34;ip:&#34; + r.Header.Get(&#34;X-Real-IP&#34;) + &#34;
&#34;for k, v := range r.Header {write = write + k + &#34;:&#34; + strings.Join(v, &#34;;&#34;) + &#34;
&#34;}_, _ = w.Write([]byte(write))}// 文件上传// 测试数据: curl http://127.0.0.1:8888/post -F file=@bank.jpg -H &#34;Referer: http://127.0.0.1/?from=/upload/&#34;func post(w http.ResponseWriter, r *http.Request) {from := r.Header.Get(&#34;Referer&#34;)from = from[strings.Index(from, &#34;=/&#34;)+1 : len(from)] //获取相对地址switch r.Method {case &#34;GET&#34;:_, _ = w.Write([]byte(string(&#34;method is GET&#34;)))case &#34;POST&#34;:_ = r.ParseForm()imgFile, _, _ := r.FormFile(&#34;file&#34;) //获取文件内容defer imgFile.Close()files := r.MultipartForm.File //获取表单中的信息imgName := files[&#34;file&#34;][0].Filename //获取表单文件中name为file的数据imgName = &#34;.&#34; + from + imgName_, err := os.Stat(imgName) //判断文件是否存在,在则隐藏并添加时间戳if err == nil {loc, _ := time.LoadLocation(&#34;Asia/Taipei&#34;)newName := &#34;.&#34; + from + &#34;.&#34; + files[&#34;file&#34;][0].Filename + &#34;.&#34; + time.Now().In(loc).Format(&#34;20060102_150405&#34;)_ = os.Rename(imgName, newName)}saveFile, _ := os.Create(imgName)_ = saveFile.Close()_, _ = io.Copy(saveFile, imgFile) //保存http.Redirect(w, r, &#34;/?from=&#34;+from, http.StatusSeeOther)default:fmt.Print(&#34;error method&#34;)}}// m3u8下载func m3u8er(w http.ResponseWriter, r *http.Request) {uri := r.PostFormValue(&#34;uri&#34;)uri, _ = url.QueryUnescape(uri)name := r.PostFormValue(&#34;name&#34;)fmt.Println(uri, &#34;:&#34;, name)_ = exec.Command(&#34;m3u8downloader&#34;, uri, name).Run()http.Redirect(w, r, &#34;/?from=/m3u8/&#34;, http.StatusSeeOther)}func noter(w http.ResponseWriter, r *http.Request) {note := r.PostFormValue(&#34;note&#34;)file, _ := os.OpenFile(&#34;/home/nginx/note/note.log&#34;, os.O_RDWR|os.O_CREATE|os.O_APPEND, 0666)loc, _ := time.LoadLocation(&#34;Asia/Taipei&#34;)_, _ = file.WriteString(time.Now().In(loc).Format(&#34;0102_1504: &#34;) + note + &#34;
&#34;)http.Redirect(w, r, &#34;/?from=/note/&#34;, http.StatusSeeOther)}
